import React, { Component } from "react";
import httpService from "../services/httpService";
import config from ".././config.json";
import { toast } from "react-toastify";
import { Link } from "react-router-dom";

class SupplierBidListRow extends Component {
  state = { bid: null };

  async componentDidMount() {
    if (!this.props.bidId) return;
    try {
      const { data } = await httpService.get(
        `${config.apiendpoint}/bids/${this.props.bidId}`
      );
      await this.setState({ bid: data.bid });
    } catch (error) {
      toast.error(error.message);
      return;
    }
  }

  render() {
    const { index } = this.props;
    const { bid } = this.state;
    if (bid === null) return null;
    return (
      <tr role="row">
        <td>{`#000${index + 1}`}</td>
        <td>
          <a>{bid.createdBy._id.toString().substring(18, 24)}</a>
        </td>
        <td>{bid.createdBy.firstName}</td>
        <td>{`${bid.totalAmount}.00 USD`}</td>
        <td>
          <span className="badge badge-primary f-14">10 Rating</span>
        </td>
        <td>
          <Link
            href="https://www.goinstablog.com/goinstablog.com/sumitdesign/design/digibids.com/suppliers-quotation"
            className="detail-icons"
          >
            View Detail
          </Link>
        </td>
      </tr>
    );
  }
}

export default SupplierBidListRow;
