import { apiendpoint } from "../config.json";
import httpService from "./httpService";
import { toast } from "react-toastify";

export async function setUpProfileService(formData) {
  await httpService.post(`${apiendpoint}/setUpProfile`, formData);
}

export async function authentication(formData) {
  try {
    const { headers } = await httpService.post(`${apiendpoint}/login`, {
      email: formData.email,
      password: formData.password,
    });
    localStorage.setItem("token", headers["x-auth-token"]);
    this.state.history.push("/customer");
  } catch (ex) {
    if (ex.response) toast.error(ex.response.data);
  }
}

export async function createAccount(formData) {
  try {
    await http.post(`${config.apiendpoint}/register`, formData);
    await http.post(`${config.apiendpoint}/otpGeneration`, {
      email: formData.email,
    });
    console.log(this.props.match.url);
    this.props.submitEmail(this.state.formData.email);
    this.props.history.push(`/register/otpVerification`);
  } catch (ex) {
    if (ex.response) toast.error(ex.response.data);
  }
}
