import React, { Component } from "react";
import { Link } from "react-router-dom";
import { paginate } from "../../utilities/paginate";
import Pagination from "../../microComponents/pagination";
import httpService from "../../services/httpService";
import { toast } from "react-toastify";
import config from "../../config.json";

class AdminCustomerList extends Component {
  state = {};

  constructor(props) {
    super(props);
    this.state.currentPage = 1;
    this.state.pageSize = 1;
    this.state.customerList = null;
    this.state.displayCustomerList = null;
  }

  componentDidMount = async () => {
    try {
      const { data } = await httpService.get(
        `${config.apiendpoint}/admin/customers`
      );
      const state = { ...this.state };
      const displayCustomerList = paginate(
        data.customerList,
        this.state.currentPage,
        this.state.pageSize
      );
      await this.setState({ displayCustomerList });
    } catch (error) {
      console.log(error);
      toast.error(error.message);
      return;
    }
  };

  handlePageChange = async (pageNumber) => {
    this.setState({ currentPage: pageNumber });
    const displayCustomerList = paginate(
      this.state.customerList,
      pageNumber,
      this.state.pageSize
    );
    await this.setState({ displayCustomerList });
  };

  renderCustomerList = () => {
    let srNo = (this.state.currentPage - 1) * this.state.pageSize;
    if (this.state.displayCustomerList === null) return;
    return this.state.displayCustomerList.map((customer) => {
      srNo++;
      return (
        <tr role="row">
          <td>{`#000${srNo}`}</td>
          <td>{customer.firstName}</td>
          <td>{customer.entityRegistrationNo}</td>
          <td>{customer.contactNumber}</td>
          <td>{`${customer.tenders.length} Tenders`}</td>
          <td>
            <span className="badge badge-primary f-14">Active</span>
          </td>
          <td>
            <Link
              to={`/admin/customerDetails/${customer._id}`}
              className="detail-icons"
            >
              <i className="fa fa-eye"></i>
            </Link>
          </td>
        </tr>
      );
    });
  };

  render() {
    if (this.state.customerList === null) return null;
    return (
      <div className="container-fluid">
        <div className="breadcrumb-header justify-content-between">
          <div className="my-auto">
            <div className="d-flex">
              <h4 className="content-title mb-0 my-auto">Page</h4>
              <span className="text-muted mt-1 tx-13 ml-2 mb-0">
                / Customer List
              </span>
            </div>
          </div>
        </div>

        <div className="row row-sm">
          <div className="col-xl-12">
            <div className="card">
              <div className="card-header pb-0">
                <div className="d-flex justify-content-between">
                  <h4 className="card-title mg-b-0 datatable-link">
                    Customer List
                  </h4>
                </div>
                <p className="tx-12 tx-gray-500 mb-2">
                  Lorem Ipsum is simply dummy text of the printing and
                  typesetting industry.
                </p>
              </div>
              <div className="card-body">
                <div className="table-responsive">
                  <div className="dataTables_wrapper dt-bootstrap4">
                    <div className="row">
                      <div className="col-sm-12">
                        <table className="table text-md-nowrap dataTable">
                          <thead>
                            <tr role="row">
                              <th>Sr No</th>
                              <th>Name</th>
                              <th>Reference No</th>
                              <th>Contact No</th>
                              <th>No Of Tenders</th>
                              <th>Status</th>
                              <th>Action</th>
                            </tr>
                          </thead>
                          <tbody>{this.renderCustomerList()}</tbody>
                        </table>
                      </div>
                    </div>
                    <div className="row">
                      <div className="col-sm-12">
                        <Pagination
                          currentPage={this.state.currentPage}
                          totalItemsCount={this.state.customerList.length}
                          pageSize={this.state.pageSize}
                          onPageChange={this.handlePageChange}
                        />
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default AdminCustomerList;
