import React, { Component } from "react";
import Form from "../macroComponents/form/form";
class UploadTenderTermsAndConditions extends Form {
  state = {
    formData: {},
    isPublished: false,
    selectedFile: null,
    errors: {},
  };

  validateOnSubmit = () => {
    if (this.state.selectedFile !== null) return null;
    if (this.state.selectedFile === null) {
      const errors = {};
      errors.selectedFile = "upload file";
      return errors;
    }
  };

  onFileChange = (event) => {
    // Update the state
    this.setState({ selectedFile: event.target.files[0] });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const errors = this.validateOnSubmit();
    await this.setState({ errors: errors || {} });

    if (errors) return;
    if (e.currentTarget.name === "publishTender") {
      const isPublished = true;
      this.setState({ isPublished });
    }
    await this.doSubmit();
  };

  doSubmit = async () => {
    this.props.onUpload(formData, this.state.isPublished);
  };

  render() {
    return (
      <div className="card">
        <div className="card-body">
          <div className="card-sigin">
            <div className="main-signup-header">
              <div className="row">
                <div className="col-md-12">
                  <div className="form-group">
                    <label>Upload terms & conditions for the tender</label>
                    <input
                      type="file"
                      className="dropify"
                      data-height="200"
                      onChange={this.onFileChange}
                    />
                  </div>
                </div>
              </div>
              <div className="row">
                <div className="col-md-6">
                  <button
                    data-target="#publishmodal"
                    data-toggle="modal"
                    className="btn btn-primary-gradient btn-block"
                    name="publishTender"
                    onClick={this.handleSubmit}
                    disabled={this.validateOnSubmit()}
                  >
                    Publish Tender
                  </button>
                </div>
                <div className="col-md-6">
                  <button
                    className="btn btn-warning-gradient btn-block"
                    onClick={this.handleSubmit}
                    name="saveForLater"
                  >
                    Save for Later
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default UploadTenderTermsAndConditions;
