import React, { Component } from "react";
import Form from "../macroComponents/form/form";
import Joi from "joi-browser";
import Input from "./input";
class AddTenderDetailsCard extends Form {
  state = {
    formData: {
      closingDate: "",
      deliveryDate: "",
      deliveryLocation: "",
      budgetAmount: "",
    },
    errors: {},
  };
  schema = {
    closingDate: Joi.date().min("now").required(),
    deliveryDate: Joi.date().greater(Joi.ref("closingDate")).required(),
    budgetAmount: Joi.number().required(),
    deliveryLocation: Joi.string().required(),
  };

  constructor(props) {
    super(props);
    this.state.formData.closingDate = this.props.formData.closingDate;
    this.state.formData.deliveryDate = this.props.formData.deliveryDate;
    this.state.formData.deliveryLocation = this.props.formData.deliveryLocation;
    this.state.formData.budgetAmount = this.props.formData.budgetAmount;
  }

  validateOnChange = (input) => {
    let obj = { [input.name]: input.value };
    let subSchema;
    if (`${[input.name]}` === "deliveryDate") {
      console.log(this.state.formData.closingDate);
      obj = {
        closingDate: this.state.formData.closingDate,
        [input.name]: input.value,
      };
      subSchema = {
        closingDate: this.schema["closingDate"],
        [input.name]: this.schema[input.name],
      };
    } else {
      subSchema = { [input.name]: this.schema[input.name] };
    }
    const { error } = Joi.validate(obj, subSchema);
    if (!error) return null;
    return error.details[0].message;
  };

  handleChange = async (e) => {
    //validation on change of input
    const errors = { ...this.state.errors };
    const errorMessage = this.validateOnChange(e.currentTarget);
    if (errorMessage) errors[e.currentTarget.name] = errorMessage;
    else delete errors[e.currentTarget.name];
    //setting state according to input
    const formData = { ...this.state.formData };
    formData[e.currentTarget.name] = e.currentTarget.value;
    await this.setState({ formData, errors });
    await this.props.updateTenderDetails(
      this.state.formData,
      this.state.errors
    );
  };

  render() {
    return (
      <div className="card">
        <div className="card-header pb-0">
          <div className="d-flex justify-content-between">
            <h4 className="card-title mg-b-0 datatable-link">
              if You want to Create New Tender. Fill the Forms !
            </h4>
          </div>
          <p className="tx-12 tx-gray-500 mb-2">
            Lorem Ipsum is simply dummy text of the printing and simply dummy
            text of the printing and typesetting industry.
          </p>
        </div>
        <div className="card-body">
          <div className="card-sigin">
            <div className="main-signup-header">
              <div className="row">
                <div className="col-md-4">
                  <Input
                    onChange={this.handleChange}
                    value={this.state.formData.closingDate}
                    label={"Closing Date"}
                    name={"closingDate"}
                    error={this.state.errors.closingDate}
                    type={"date"}
                  />
                </div>
                <div className="col-md-4">
                  {this.renderInput(
                    "deliveryDate",
                    "Tender Delivery Date",
                    "date"
                  )}
                </div>
                <div className="col-md-4">
                  {this.renderInput("budgetAmount", "Tender Budget Amount")}
                </div>
              </div>
              <div className="row">
                <div className="col-md-12">
                  {this.renderInput(
                    "deliveryLocation",
                    "Tender Delivery Location"
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default AddTenderDetailsCard;
