import React from "react";
const Input = ({ name, value, label, onChange }) => {
  return (
    <React.Fragment>
      <label htmlFor={name}>{label}</label>{" "}
      <input
        className="form-control"
        placeholder={`Enter your ${label}`}
        onChange={onChange}
        value={value}
        id={name}
        name={name}
        type={name == /password/i ? "password" : "text"}
      />
    </React.Fragment>
  );
};

export default Input;
