import React, { Component } from "react";
import { Route, Switch, Redirect } from "react-router-dom";
import NotFound from "./notfound";
import LoginForm from "./loginForm";
import CreateAccount from "./macroComponents/createAccount";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import OtpVerificationForm from "./macroComponents/otpVerification";
import ForgotPassword from "./macroComponents/forgotPasswordForm";
import Register from "./register.jsx";

class App extends Component {
  state = {
    register: {
      email: "",
    },
  };

  getEmail = async (email) => {
    const register = { email: email };
    await this.setState({ register });
  };

  render() {
    return (
      <React.Fragment>
        <ToastContainer />
        <Switch>
          <Route exact path="/login" component={LoginForm} />
          <Route
            exact
            path="/register"
            render={(props) => <Register {...props} />}
          />
          <Route
            exact
            path={`/otpVerification`}
            render={(props) => (
              <OtpVerificationForm
                email={this.state.register.email}
                {...props}
              />
            )}
          />
          <Route path="/forgotPassword" component={ForgotPassword} />
          <Route path="/not-found" component={NotFound} />
          <Redirect exact from="/createAccount" to="/register" />
          <Redirect exact from="/signIn" to="/login" />
          <Redirect to="/not-found" />
        </Switch>
      </React.Fragment>
    );
  }
}

export default App;
