import React, { Component } from "react";
import Form from "../macroComponents/form/form";
import Joi from "joi-browser";
class AddItemCard extends Form {
  state = {
    formData: {
      selectCategory: "",
      itemName: "",
      QuantityOfItem: "",
      unitOfMeassure: "",
    },
  };

  schema = {
    selectCategory: Joi.string().required(),
    itemName: Joi.string().required(),
    addQuantityForItem: Joi.string().required(),
    unitOfMeassure: Joi.string().required(),
  };

  render() {
    return (
      <div className="card">
        <div className="card-body">
          <div className="card-sigin">
            <div className="main-signup-header">
              <div className="row">
                <div className="col-md-6">
                  <label>Select Category</label>
                  <select className="form-control select2-no-search ">
                    <option> Mobile </option>
                    <option> Laptop </option>
                  </select>
                </div>
                <div className="col-md-6">
                  <div className="form-group">
                    <label>Item Name</label>
                    <input
                      className="form-control"
                      placeholder="Enter Item name"
                      type="text"
                    />
                  </div>
                </div>
              </div>
              <div className="row">
                <div className="col-md-6">
                  {this.renderInput("quanityOfItem", "Add Quantity for Item")}
                  <div className="form-group">
                    <label>Add Quantity for Item</label>
                    <input
                      className="form-control"
                      placeholder="00"
                      type="text"
                    />
                  </div>
                </div>
                <div className="col-md-6">
                  {this.renderInput("unitOfMeasure", "Unit of Meassure")}
                </div>
              </div>
              <div className="row">
                <div className="col-md-3 offset-9">
                  {this.renderButton(
                    "Add More",
                    this.handleSubmit,
                    "btn btn-warning-gradient btn-block"
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default AddItemCard;
