import React, { Component } from "react";

import SupplierDetailsCards from "./supplierDetailsCards";
import SupplierDetails from "./supplierDetails";

class SupplierDetailsContainer extends Component {
  state = {
    userCurrentStatus: null,
  };
  constructor(props) {
    super(props);
    this.state.userCurrentStatus = this.props.supplier.user.isApproved;
  }
  handleUserStatus = async (e) => {
    let previousState = this.state.userCurrentStatus;
    try {
      let isApproved;
      if (e.currentTarget.name === "approve") {
        isApproved = true;
      } else isApproved = false;
      this.setState({ userCurrentStatus: isApproved });
      const { data, error } = await httpService.post(
        `${config.apiendpoint}/admin/userChangeStatus`,
        {
          userId: this.props.customer.user._id,
          status: isApproved,
        }
      );
      toast.success(data.message);
      if (!data.message) toast.success(data);
    } catch (error) {
      if (error.response) {
        toast.error(error.response.data);
        this.setState({ userCurrentStatus: previousState });
      }
    }
  };
  render() {
    return (
      <div className="container-fluid">
        <div className="breadcrumb-header justify-content-between">
          <div className="my-auto">
            <div className="d-flex">
              <h4 className="content-title mb-0 my-auto">Page</h4>
              <span className="text-muted mt-1 tx-13 ml-2 mb-0">
                / Supplier Detail
              </span>
            </div>
          </div>
        </div>
        <SupplierDetailsCards supplier={this.props.supplier} />
        <div class="breadcrumb-header justify-content-between">
          <div class="my-auto">
            <div class="d-flex">
              <h4 class="content-title mb-0 my-auto">Supplier Status :</h4>
            </div>
          </div>
          <div class="d-flex my-xl-auto right-content">
            <div class="pr-1 mb-3 mb-xl-0">
              <a href="#" type="button" class="btn btn-primary ">
                <i class="fa fa-check"></i> Approved
              </a>
              <a href="#" type="button" class="btn btn-danger ">
                <i class="fa fa-times"></i> Reject
              </a>
              <a href="#" type="button" class="btn btn-success ">
                <i class="fa fa-check-circle"></i> Accept
              </a>
            </div>
          </div>
        </div>
        <SupplierDetails
          supplier={this.props.supplier}
          tenderClicked={(tenderId) => this.props.tenderClicked(tenderId)}
        />
      </div>
    );
  }
}

export default SupplierDetailsContainer;
