import React, { Component } from "react";
import Form from "../macroComponents/form/form";
class UploadTenderTermsAndConditions extends Form {
  state = { selectedFile: null };

  validateOnSubmit = () => {
    if (this.state.selectedFile1 !== null) return null;
    const errors = {};

    if (this.state.selectedFile1 === null) {
      const errors = {};
      errors.selectedFile1 = "upload file";
      return errors;
    }
  };

  onFileChange = (event) => {
    // Update the state
    this.setState({ selectedFile: event.target.files[0] });
  };

  doSubmit = async () => {
    const formData = new FormData();
    for (const item in this.state.formData) {
      formData.append(item, this.state.formData[item]);
    }

    formData.append(
      "myFile1",
      this.state.selectedFile1,
      this.state.selectedFile1.name
    );
    if (this.state.selectedFile2 !== null) {
      formData.append(
        "myFile2",
        this.state.selectedFile2,
        this.state.selectedFile2.name
      );
    }
    console.log(formData);
    const { data, error } = await registerService.setUpProfileService(formData);
    if (!data.bankDetailsStatus) {
      this.props.submitProfileDetails(this.state.formData);
      this.props.history.push(`/register/uploadBankDetails`);
    } else {
      toast.success("registration process already completed");
      this.props.history.push(`/`);
    }
    if (error) {
      return;
    }
  };

  render() {
    return (
      <div className="card">
        <div className="card-body">
          <div className="card-sigin">
            <div className="main-signup-header">
              <div className="row">
                <div className="col-md-12">
                  <div className="form-group">
                    <label>Upload terms & conditions for the tender</label>
                    <input
                      type="file"
                      className="dropify"
                      data-height="200"
                      onChange={this.onFileChange}
                    />
                  </div>
                </div>
              </div>
              <div className="row">
                <div className="col-md-6">
                  <button
                    data-target="#publishmodal"
                    data-toggle="modal"
                    className="btn btn-primary-gradient btn-block"
                  >
                    Publish Tender
                  </button>
                </div>
                <div className="col-md-6">
                  <button className="btn btn-warning-gradient btn-block">
                    Save for Later
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default UploadTenderTermsAndConditions;
