import React, { Component } from "react";
import { Link } from "react-router-dom";
class DisplayTenderItemList extends Component {
  state = {};

  displayItem = () => {
    let itemNo = 0;
    return this.props.itemList.map((item) => {
      const index = itemNo;
      itemNo++;
      return (
        <tr role="row">
          <td>{`#000${itemNo}`}</td>
          <td>{item.selectCategory}</td>
          <td>{item.itemName} </td>
          <td>{item.quantityOfItem}</td>
          <td>{item.unitOfMeasure}</td>
          <td>
            <Link
              to="#"
              className="detail-icons"
              onClick={(index) => this.deleteItem(index)}
            >
              <i className="fa fa-trash"></i>
            </Link>
          </td>
        </tr>
      );
    });
  };

  deleteItem(index) {
    console.log(index);
  }

  render() {
    return (
      <div className="card">
        <div className="card-header pb-0">
          <div className="d-flex justify-content-between">
            <h4 className="card-title mg-b-0 datatable-link">Item List</h4>
          </div>
          <p className="tx-12 tx-gray-500 mb-2">
            Lorem Ipsum is simply dummy text of the printing and typesetting
            industry.
          </p>
        </div>
        <div className="card-body">
          <div className="table-responsive">
            <div>
              <div className="row">
                <div className="col-sm-12">
                  <table className="table text-md-nowrap">
                    <thead>
                      <tr role="row">
                        <th>Sr no</th>
                        <th>Catehory</th>
                        <th>Item Name</th>
                        <th>Quantity</th>
                        <th>Unit of Meassure</th>
                        <th>Status</th>
                      </tr>
                    </thead>
                    <tbody>{this.displayItem()}</tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default DisplayTenderItemList;
