import React from "react";
import Form from "./form/form";
import Joi from "joi-browser";
import { Link } from "react-router-dom";
import httpService from "../services/httpService";
import { apiendpoint } from "../config.json";

class ProfileSetup extends Form {
  state = {
    formData: {
      profileType: "",
      organisationType: "",
      firstName: "",
      lastName: "",
      idNumber: "",
      contactNumber: "",
      companyName: "",
      entityRegistrationNo: "",
      vatRegistration: "",
      vatNumber: "",
      tradingAs: "",
      website: "",
      physicalAddress: "",
      postalAddress: "",
      contactPerson: "",
      contactNo: "",
      seletectedFile: [],
    },

    errors: {},
  };
  schema = {
    profileType: Joi.string().required(),
    organisationType: Joi.string().required(),
    firstName: Joi.string().required(),
    lastName: Joi.string().required(),
    idNumber: Joi.string().required(),
    contactNumber: Joi.string().required(),
    companyName: Joi.string().required(),
    entityRegistrationNo: Joi.string().required(),
    vatRegistration: Joi.string().required(),
    vatNumber: Joi.string().required(),
    tradingAs: Joi.string().required(),
    website: Joi.string().required(),
    physicalAddress: Joi.string().required(),
    postalAddress: Joi.string().required(),
    contactPerson: Joi.string().required(),
    contactNo: Joi.string().required(),
    selectedFile: Joi.Array(),
  };

  validateOnSubmit = () => {
    const result = Joi.validate(this.state.formData, this.schema, {
      abortEarly: false,
    });
    if (!result.error && this.state.formData.selectedFile.length !== 0)
      return null;
    const errors = {};
    if (result.error) {
      for (let item of result.error.details) {
        errors[item.path[0]] = item.message;
      }
      return errors;
    }
    if (this.state.formData.selectedFile.length === 0) {
      const errors = {};
      errors.selectedFile = "upload file";
      return errors;
    }
  };

  onFileChange = (event) => {
    // Update the state
    this.setState({ selectedFile1: event.target.files[0] });
  };

  doSubmit = async () => {
    // Create an object of formData
    const registerFormData = new FormData();

    // Update the formData object

    // Details of the uploaded file
    //console.log(this.state.selectedFile1);
    //console.log(this.state.selectedFile2);

    // Request made to the backend api
    // Send formData object

    for (const item in this.state.formData) {
      await registerFormData.append(item, this.state.formData.item);
    }
    await fileData.append(
      "myFile1",
      this.state.selectedFile1,
      this.state.selectedFile1.name
    );
    if (this.state.selectedFile2 !== null) {
      await fileData.append(
        "myFile2",
        this.state.selectedFile2,
        this.state.selectedFile2.name
      );
    }

    console.log(fileData);
    httpService.post(`${apiendpoint}/setUpProfile`);
  };

  render() {
    return (
      <div class="page">
        <div class="container-fluid">
          <div class="row no-gutter">
            <div class="col-md-12 col-lg-12 col-xl-12 bg-white">
              <div class="login d-flex align-items-center py-2 p-top-50">
                <div class="container p-0">
                  <div class="row">
                    <div class="col-md-12 col-lg-12 col-xl-12 mx-auto">
                      <div class="card-sigin">
                        <div class="mb-2 d-flex">
                          <Link to="#">
                            <img
                              src="https://www.goinstablog.com/goinstablog.com/sumitdesign/design/digibids.com/common/img/logo/logo.png"
                              class="sign-favicon"
                              alt="logo"
                            />
                          </Link>
                        </div>
                        <div class="card-sigin">
                          <div class="main-signup-header">
                            <h2>Profile Setup</h2>
                            <h5 class="font-weight-semibold mb-4">
                              Lorem Ipsum is simply dummy text of the printing
                              and typesetting industry. Lorem Ipsum has been the
                              industry's standard dummy text
                            </h5>
                            <form>
                              <div class="row">
                                <div class="col-md-6">
                                  {this.renderSelect(
                                    "profileType",
                                    "Profile Type",
                                    [
                                      { _id: 1, name: "customer" },
                                      { _id: 2, name: "suppliers" },
                                    ]
                                  )}
                                </div>
                                <div class="col-md-6">
                                  {this.renderSelect(
                                    "organisationType",
                                    "Organisation Type",
                                    [
                                      { _id: 1, name: "Private Limited" },
                                      { _id: 2, name: "Government" },
                                    ]
                                  )}
                                </div>
                              </div>
                              <div class="row">
                                <div class="col-md-6">
                                  {this.renderInput("firstName", "First Name")}
                                </div>
                                <div class="col-md-6">
                                  {this.renderInput("lastName", "Last Name")}
                                </div>
                              </div>
                              <div class="row">
                                <div class="col-md-6">
                                  {this.renderInput("idNumber", "Id Number")}
                                </div>
                                <div class="col-md-6">
                                  {this.renderInput(
                                    "contactNumber",
                                    "Contact Number"
                                  )}
                                </div>
                              </div>
                              <div class="row">
                                <div class="col-md-6">
                                  {this.renderInput(
                                    "companyName",
                                    "Company Name"
                                  )}
                                </div>
