import React, { Component } from "react";
import httpService from "../services/httpService";
import config from "../config.json";
import { toast } from "react-toastify";
import SupplierBidCards from "./supplierBidCards";
class SupplierBidDetails extends Component {
  state = { bid: null };

  async componentDidMount() {
    if (!this.props.tenderId) return;
    try {
      const { data } = await httpService.get(
        `${config.apiendpoint}/supplier/myBidDetails/${this.props.tenderId}`
      );
      await this.setState({ bid: data.bid });
      console.log(this.state.bid);
    } catch (error) {
      toast.error(error.message);
      return;
    }
  }

  renderBidItemDetails = () => {
    if (this.state.bid === null) return null;
    let srNo;
    return this.state.bid.itemList.map((item) => {
      srNo++;
      return (
        <tr role="row">
          <td>{`#000${srNo}`}</td>
          <td>${item.category}</td>
          <td>${item.name}</td>
          <td>
            <span class="badge badge-primary f-14">{$item.unitOfMeasure}</span>
          </td>
          <td>
            <span class="badge badge-success f-14">10 Unit</span>
          </td>
          <td>"price in the bid"</td>
          <td>100 </td>
          <td>9.95 </td>
          <td>99.9 </td>
        </tr>
      );
    });
  };

  render() {
    if (this.state.bid === null) return null;
    return (
      <div class="container-fluid">
        <div class="breadcrumb-header justify-content-between">
          <div class="my-auto">
            <div class="d-flex">
              <h4 class="content-title mb-0 my-auto">Bid</h4>
              <span class="text-muted mt-1 tx-13 ml-2 mb-0">
                /My Bid Details
              </span>
            </div>
          </div>
        </div>
        <SupplierBidCards tender={this.state.bid.tender} />
        <div class="row row-sm">
          <div class="col-xl-12">
            <div class="card">
              <div class="card-header pb-0">
                <div class="d-flex justify-content-between">
                  <h4 class="card-title mg-b-0 datatable-link">
                    Tender Products List
                  </h4>
                </div>
                <p class="tx-12 tx-gray-500 mb-2">
                  Lorem Ipsum is simply dummy text of the printing and
                  typesetting industry.
                </p>
              </div>
              <div class="card-body">
                <div class="table-responsive">
                  <div
                    id="example1_wrapper"
                    class="dataTables_wrapper dt-bootstrap4"
                  >
                    <div class="row">
                      <div class="col-sm-12">
                        <table
                          class="table text-md-nowrap dataTable"
                          id="example1"
                        >
                          <thead>
                            <tr role="row">
                              <th>Sr. No</th>
                              <th>Category</th>
                              <th>Item / Description</th>
                              <th>Unit of Meassure</th>
                              <th>Qty</th>
                              <th>Price</th>
                              <th>Total</th>
                              <th>Lowest Price</th>
                              <th>Lowest Total</th>
                            </tr>
                          </thead>
                          <tbody>
                            <tr role="row">
                              <td>#Q0001</td>
                              <td>Mobile</td>
                              <td>iPhone X </td>
                              <td>
                                <span class="badge badge-primary f-14">
                                  10 Unit
                                </span>
                              </td>
                              <td>
                                <span class="badge badge-success f-14">
                                  10 Unit
                                </span>
                              </td>
                              <td>"price in the bid"</td>
                              <td>100 </td>
                              <td>9.95 </td>
                              <td>99.9 </td>
                            </tr>
                          </tbody>
                        </table>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default SupplierBidDetails;
