import React, { Component } from "react";
import Form from "../macroComponents/form/form";
class AddTenderDetailsCard extends Form {
  state = {
    formData: {
      tenderClosingDate: "",
      tenderDeliveryDate: "",
      tenderDeliveryLocation: "",
      tenderBudgetAmount: "",
      itemList: [
        {
          selectCategory: "",
          itemName: "",
          addQuantityForItem: "",
          unitOfMeassure: "",
        },
      ],
    },
    errors: {},
  };

  schema = {
    tenderClosingDate: Joi.date().required(),
    tenderDeliveryDate: Joi.date().required(),
    tenderBudgetAmount: Joi.string().required(),
    tenderDeliveryLocation: Joi.string().required(),
    itemList: Joi.array().items(
      Joi.object({
        selectCategory: Joi.string().required(),
        itemName: Joi.string().required(),
        addQuantityForItem: Joi.string().required(),
        unitOfMeassure: Joi.string().required(),
      })
    ),
  };
  render() {
    return (
      <div className="card">
        <div className="card-header pb-0">
          <div className="d-flex justify-content-between">
            <h4 className="card-title mg-b-0 datatable-link">
              if You want to Create New Tender. Fill the Forms !
            </h4>
          </div>
          <p className="tx-12 tx-gray-500 mb-2">
            Lorem Ipsum is simply dummy text of the printing and simply dummy
            text of the printing and typesetting industry.
          </p>
        </div>
        <div className="card-body">
          <div className="card-sigin">
            <div className="main-signup-header">
              <div className="row">
                <div className="col-md-4">
                  {this.renderInput(
                    "tenderClosingDate",
                    "Tender Closing Date",
                    "date"
                  )}
                </div>
                <div className="col-md-4">
                  {this.renderInput(
                    "tenderDeliveryDate",
                    "Tender Delivery Date",
                    "date"
                  )}
                </div>
                <div className="col-md-4">
                  {this.renderInput(
                    "tenderBudgetAmount",
                    "Tender Budget Amount"
                  )}
                </div>
              </div>
              <div className="row">
                <div className="col-md-12">
                  <div className="form-group">
                    <label>Tender Delivery Location</label>
                    <input
                      className="form-control"
                      placeholder="Location"
                      type="text"
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default AddTenderDetailsCard;
