import React, { Component } from "react";
import httpService from "../services/httpService";
import config from "../config.json";
import { toast } from "react-toastify";
import SupplierBidCards from "./supplierBidCards";
import Form from "../macroComponents/form/form";
import Joi from "joi-browser";
import { Link } from "react-router-dom";

class SupllierBid extends Form {
  state = {
    tender: null,
    formData: {
      pricesList: "",
    },
    errors: {},
  };

  schema = {
    prices: Joi.array()
      .items(
        Joi.object({
          price: Joi.number().required(),
        })
      )
      .min(1),
  };

  async componentDidMount() {
    try {
      const { data } = await httpService.get(
        `${config.apiendpoint}/tenders/${this.props.tenderId}`
      );
      this.setState({ tender: data.tender });
    } catch (error) {
      toast.error(error.message);
      return;
    }
  }

  renderItemList = () => {
    const { tender } = this.state;
    let srNo = 0;
    return tender.itemList.map((item) => {
      srNo++;
      return (
        <tr role="row">
          <td>{`#000${srNo}`}</td>
          <td>{item.category}</td>
          <td>{item.name}</td>
          <td>
            <span className="badge badge-primary f-14">
              {item.unitOfMeasure}
            </span>
          </td>
          <td>
            <span className="badge badge-success f-14">{item.quantity}</span>
          </td>
          <td>
            <div>
              <input
                onChange={this.handleChange}
                value={this.state.formData["price"]}
                type="text"
                className="form-control"
                placeholder=""
                id="price"
                name="price"
              />
              {this.state.errors["price"] && (
                <div className="alert alert-danger">
                  {this.state.errors["price"]}
                </div>
              )}
            </div>
          </td>
          <td>100 </td>
        </tr>
      );
    });
  };

  render() {
    if (this.state.tender === null) return null;
    return (
      <div className="container-fluid">
        <div className="breadcrumb-header justify-content-between">
          <div className="my-auto">
            <div className="d-flex">
              <h4 className="content-title mb-0 my-auto">Tender</h4>
              <span className="text-muted mt-1 tx-13 ml-2 mb-0">
                / Tender Detail
              </span>
            </div>
          </div>
        </div>
        <SupplierBidCards tender={this.state.tender} />
        <div className="row row-sm">
          <div className="col-xl-12">
            <div className="card">
              <div className="card-header pb-0">
                <div className="d-flex justify-content-between">
                  <h4 className="card-title mg-b-0 datatable-link">
                    Tender Products List
                  </h4>
                </div>
                <p className="tx-12 tx-gray-500 mb-2">
                  Lorem Ipsum is simply dummy text of the printing and
                  typesetting industry.
                </p>
              </div>
              <div className="card-body">
                <div className="table-responsive">
                  <div
                    id="example1_wrapper"
                    className="dataTables_wrapper dt-bootstrap4"
                  >
                    <div className="row">
                      <div className="col-sm-12">
                        <table
                          className="table text-md-nowrap dataTable"
                          id="example1"
                        >
                          <thead>
                            <tr role="row">
                              <th>Sr. No</th>
                              <th>Category</th>
                              <th>Item / Description</th>
                              <th>Unit of Meassure</th>
                              <th>Qty</th>
                              <th>Price</th>
                              <th>Total</th>
                            </tr>
                          </thead>
                          <tbody>{this.renderItemList()} </tbody>
                        </table>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col-md-6">
            <div className="form-group">
              <label className="check-position">
                <input type="checkbox" name="" className="check-term" />I accept
                <a href="https://www.goinstablog.com/goinstablog.com/sumitdesign/design/digibids.com/supplier/terms-and-condition">
                  Terms & Conditions
                </a>
              </label>
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col-md-4">
            <Link to="/supplier" className="btn btn-warning-gradient btn-block">
              Ignore
            </Link>
          </div>
          <div className="col-md-4">
            <a href="#" className="btn btn-primary-gradient btn-block">
              Save Draft
            </a>
          </div>
          <div className="col-md-4">
            <a href="#" className="btn btn-warning-gradient btn-block">
              Submit
            </a>
          </div>
        </div>
      </div>
    );
  }
}

export default SupllierBid;
