import React, { Component } from "react";
import httpService from "../../services/httpService";
import config from "../../config.json";
import { toast } from "react-toastify";

class CustomerReceiverlist extends Component {
  state = {
    receiverList: null,
    displayReceiverList: null,
    currentPage: null,
    pageSize: null,
  };

  constructor(props) {
    super(props);
    this.state.currentPage = 1;
    this.state.pageSize = 4;
  }
  async componentDidMount() {
    try {
      const { data } = await httpService.get(
        `${config.apiendpoint}/customer/receiverList`
      );
      const { receiversList } = data;
      await this.setState({ receiversList });
      const displayReceiverList = paginate(
        this.state.receiverList,
        this.state.currentPage,
        this.state.pageSize
      );
      await this.setState({ displayReceiverList });
    } catch (error) {
      toast.error(error.message);
      return;
    }
  }

  handlePageChange = async (pageNumber) => {
    this.setState({ currentPage: pageNumber });
    const displayReceiverList = paginate(
      this.state.receiverList,
      pageNumber,
      this.state.pageSize
    );
    await this.setState({ displayReceiverList });
  };

  renderReceiverTable = () => {
    let srNo = (this.state.currentPage - 1) * this.state.pageSize;

    let receiverList = this.state.displayReceiverList;
    if (receiverList === null) return;
    return receiverList.map((tender) => {
      srNo++;
      return (
        <tr role="row">
          <td>{`#000${srNo}`}</td>
          <td>
            <Link to={`/customer/tenderDetails/${tender._id}`}>
              {tender._id.toString().substring(18, 24)}
            </Link>
          </td>
          <td>{tender.budgetAmount}</td>
          <td>{tender.creationDate.toString().substring(0, 10)}</td>
          <td>{tender.deliveryDate.toString().substring(0, 10)}</td>
          <td>{tender.closingDate.toString().substring(0, 10)}</td>
          <td>
            <span className={`badge badge-primary f-14`}>{tender.status}</span>
          </td>
        </tr>
      );
    });
  };

  render() {
    return (
      <div className="container-fluid">
        <div className="breadcrumb-header justify-content-between">
          <div className="my-auto">
            <div className="d-flex">
              <h4 className="content-title mb-0 my-auto">Tender</h4>
              <span className="text-muted mt-1 tx-13 ml-2 mb-0">
                / Receiver List
              </span>
            </div>
          </div>
        </div>

        <div className="row row-sm">
          <div className="col-xl-12">
            <div className="card">
              <div className="card-header pb-0">
                <div className="d-flex justify-content-between">
                  <h4 className="card-title mg-b-0 datatable-link">
                    Your Receiver List
                  </h4>
                </div>
                <p className="tx-12 tx-gray-500 mb-2">
                  Lorem Ipsum is simply dummy text of the printing and
                  typesetting industry.
                </p>
              </div>
              <div className="card-body">
                <div className="table-responsive">
                  <div className="dataTables_wrapper dt-bootstrap4">
                    <div className="row">
                      <div className="col-sm-12">
                        <table className="table text-md-nowrap dataTable">
                          <thead>
                            <tr role="row">
                              <th>Code No</th>
                              <th>Conatact Person</th>
                              <th>Conatact Number</th>
                              <th>Email</th>
                              <th>Postal Address</th>
                              <th>Action</th>
                            </tr>
                          </thead>
                          <tbody>
                            <tr role="row">
                              <td>#C0001</td>
                              <td>Mo Danish</td>
                              <td>+76 6767 5757</td>
                              <td>danish@gmail.com</td>
                              <td>176 STF Road Hamiltom</td>
                              <td>
                                <div class="main-toggle main-toggle-success on">
                                  <span></span>
                                </div>
                              </td>
                            </tr>
                          </tbody>
                        </table>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default CustomerReceiverlist;
